import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { Upload, Send, FileText, Bot, User, AlertCircle, Plus, Search, Settings, Trash2, Edit, MessageCircle, Star, ThumbsUp, ThumbsDown, Copy, MoreHorizontal, RotateCcw } from 'lucide-react';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'bot';
  timestamp: Date;
  isRelated?: boolean;
  confidence?: number;
}

interface Session {
  sessionId: string;
  filename: string;
}

interface Conversation {
  id: string;
  title: string;
  timestamp: Date;
  isActive?: boolean;
}

function App() {
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [session, setSession] = useState<Session | null>(null);
  const [uploadedFile, setUploadedFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [conversations, setConversations] = useState<Conversation[]>([]);
  
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Add welcome message
  useEffect(() => {
    if (messages.length === 0) {
      setMessages([
        {
          id: '1',
          text: "Create a chatbot gpt using python language what will be step for that",
          sender: 'user',
          timestamp: new Date(),
        },
        {
          id: '2',
          text: `Here are the steps to create a chatbot using GPT in Python:

1. Install Required Libraries
   - pip install openai
   - pip install python-dotenv

2. Set Up OpenAI API
   - Get API key from OpenAI
   - Create .env file with your API key

3. Create Basic Chatbot
   - Import openai library
   - Configure API key
   - Create chat function

4. Implement Conversation Loop
   - Add user input handling
   - Process responses
   - Add conversation history

5. Add Error Handling
   - Handle API errors
   - Add rate limiting
   - Implement fallback responses

6. Enhance with Features
   - Add context memory
   - Implement conversation saving
   - Add user authentication

7. Deploy and Test
   - Test with various inputs
   - Optimize performance
   - Deploy to production`,
          sender: 'bot',
          timestamp: new Date(),
        },
        {
          id: '3',
          text: "What is use of that chatbot?",
          sender: 'user',
          timestamp: new Date(),
        },
        {
          id: '4',
          text: "Chatbots can be used for a wide range of purposes, including:",
          sender: 'bot',
          timestamp: new Date(),
        }
      ]);
    }
  }, []);

  // Mock conversations for the sidebar
  useEffect(() => {
    setConversations([
      { id: '1', title: 'Create Chatbot GPT...', timestamp: new Date(), isActive: true },
      { id: '2', title: 'Create Html Game Environment...', timestamp: new Date() },
      { id: '3', title: 'Apply To Leave For Emergency', timestamp: new Date() },
      { id: '4', title: 'Crypto Lending App Name', timestamp: new Date() },
      { id: '5', title: 'Operator Grammar Types', timestamp: new Date() },
      { id: '6', title: 'Min States For Binary DFA', timestamp: new Date() },
    ]);
  }, []);

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Validate file type
    const allowedTypes = ['application/pdf', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document', 'text/plain'];
    if (!allowedTypes.includes(file.type)) {
      setError('Please upload a PDF, Word document, or text file.');
      return;
    }

    setUploadedFile(file);
    setIsUploading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setSession({
        sessionId: response.data.session_id,
        filename: response.data.filename,
      });

      // Add success message
      setMessages(prev => [
        ...prev,
        {
          id: Date.now().toString(),
          text: `ðŸ“„ Great! I've uploaded "${response.data.filename}" and I'm ready to help you understand it. Feel free to ask me anything about the document - I can summarize, explain, count items, or answer any questions you have!`,
          sender: 'bot',
          timestamp: new Date(),
        }
      ]);

    } catch (error: any) {
      setError(error.response?.data?.detail || 'Failed to upload document. Please try again.');
    } finally {
      setIsUploading(false);
    }
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim() || !session) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputMessage,
      sender: 'user',
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);
    setError(null);

    try {
      const response = await axios.post('/chat', {
        message: inputMessage,
        session_id: session.sessionId,
      });

      const botMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: response.data.response,
        sender: 'bot',
        timestamp: new Date(),
        isRelated: response.data.is_related,
        confidence: response.data.confidence,
      };

      setMessages(prev => [...prev, botMessage]);

    } catch (error: any) {
      setError(error.response?.data?.detail || 'Failed to get response. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSendMessage();
    }
  };

  const resetSession = () => {
    setSession(null);
    setUploadedFile(null);
    setMessages([
      {
        id: '1',
        text: "ðŸ‘‹ Hi there! I'm your AI document assistant. I can help you understand and analyze any document you upload. Just share a PDF, Word document, or text file, and I'll be happy to chat with you about its content!",
        sender: 'bot',
        timestamp: new Date(),
      }
    ]);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex">
      {/* Left Sidebar */}
      <div className="w-80 bg-white shadow-lg flex flex-col">
        {/* Header */}
        <div className="p-6 border-b border-gray-100">
          <h1 className="text-2xl font-bold text-gray-900">CHAT A.I+</h1>
        </div>

        {/* New Chat & Search */}
        <div className="p-4 space-y-3">
          <button className="w-full bg-gradient-to-r from-purple-600 to-blue-600 text-white px-4 py-3 rounded-xl font-medium flex items-center justify-center space-x-2 hover:from-purple-700 hover:to-blue-700 transition-all">
            <Plus className="h-5 w-5" />
            <span>+ New chat</span>
          </button>
          <button className="w-10 h-10 bg-gray-800 text-white rounded-full flex items-center justify-center hover:bg-gray-700 transition-colors">
            <Search className="h-5 w-5" />
          </button>
        </div>

        {/* Conversations */}
        <div className="flex-1 overflow-y-auto px-4 sidebar-scroll">
          <div className="flex items-center justify-between mb-4">
            <span className="text-sm text-gray-500 font-medium">Your conversations</span>
            <button className="text-sm text-gray-400 hover:text-gray-600">Clear All</button>
          </div>
          
          <div className="space-y-2">
            {conversations.map((conversation) => (
              <div
                key={conversation.id}
                className={`flex items-center justify-between p-3 rounded-lg cursor-pointer transition-all ${
                  conversation.isActive 
                    ? 'bg-purple-50 border border-purple-200' 
                    : 'hover:bg-gray-50'
                }`}
              >
                <div className="flex items-center space-x-3 flex-1 min-w-0">
                  <MessageCircle className={`h-4 w-4 ${conversation.isActive ? 'text-purple-600' : 'text-gray-400'}`} />
                  <span className={`text-sm truncate ${conversation.isActive ? 'text-purple-900 font-medium' : 'text-gray-700'}`}>
                    {conversation.title}
                  </span>
                  {conversation.isActive && (
                    <div className="w-2 h-2 bg-purple-600 rounded-full"></div>
                  )}
                </div>
                {conversation.isActive && (
                  <div className="flex items-center space-x-1">
                    <button className="p-1 hover:bg-gray-200 rounded">
                      <Edit className="h-3 w-3 text-gray-500" />
                    </button>
                    <button className="p-1 hover:bg-gray-200 rounded">
                      <Trash2 className="h-3 w-3 text-gray-500" />
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>

          <div className="mt-6 pt-4 border-t border-gray-100">
            <span className="text-xs text-gray-400 font-medium">Last 7 Days</span>
          </div>
        </div>

        {/* User & Settings */}
        <div className="p-4 border-t border-gray-100 space-y-3">
          <button className="w-full flex items-center space-x-3 p-3 rounded-lg hover:bg-gray-50 transition-colors">
            <Settings className="h-5 w-5 text-gray-500" />
            <span className="text-sm text-gray-700">Settings</span>
          </button>
          <div className="flex items-center space-x-3 p-3">
            <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center">
              <span className="text-white text-sm font-medium">AN</span>
            </div>
            <span className="text-sm text-gray-700 font-medium">Andrew Neilson</span>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Chat Messages */}
        <div className="flex-1 overflow-y-auto p-6 space-y-6 main-scroll">
          {messages.map((message) => (
            <div
              key={message.id}
              className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div className="max-w-2xl">
                <div className="flex items-start space-x-3">
                  {message.sender === 'user' ? (
                    <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center flex-shrink-0">
                      <span className="text-white text-sm font-medium">AN</span>
                    </div>
                  ) : (
                    <div className="w-8 h-8 bg-gradient-to-r from-purple-600 to-blue-600 rounded-full flex items-center justify-center flex-shrink-0">
                      <span className="text-white text-sm font-bold">AI</span>
                    </div>
                  )}
                  <div className="flex-1">
                    <div className={`px-4 py-3 rounded-2xl ${
                      message.sender === 'user'
                        ? 'bg-gradient-to-r from-purple-600 to-blue-600 text-white'
                        : 'bg-white text-gray-900 shadow-sm border border-gray-100'
                    }`}>
                      <div className="text-sm whitespace-pre-line leading-relaxed">{message.text}</div>
                    </div>
                    
                    {/* AI Response Actions */}
                    {message.sender === 'bot' && (
                      <div className="flex items-center space-x-4 mt-3 ml-4">
                        <div className="flex items-center space-x-2">
                          <button className="p-1 hover:bg-gray-100 rounded">
                            <ThumbsUp className="h-4 w-4 text-gray-400" />
                          </button>
                          <button className="p-1 hover:bg-gray-100 rounded">
                            <ThumbsDown className="h-4 w-4 text-gray-400" />
                          </button>
                          <button className="p-1 hover:bg-gray-100 rounded">
                            <Copy className="h-4 w-4 text-gray-400" />
                          </button>
                          <button className="p-1 hover:bg-gray-100 rounded">
                            <MoreHorizontal className="h-4 w-4 text-gray-400" />
                          </button>
                        </div>
                        <button className="flex items-center space-x-2 text-sm text-gray-500 hover:text-gray-700">
                          <RotateCcw className="h-4 w-4" />
                          <span>Regenerate</span>
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          ))}
          
          {/* Loading indicator */}
          {isLoading && (
            <div className="flex justify-start">
              <div className="max-w-2xl">
                <div className="flex items-start space-x-3">
                  <div className="w-8 h-8 bg-gradient-to-r from-purple-600 to-blue-600 rounded-full flex items-center justify-center flex-shrink-0">
                    <span className="text-white text-sm font-bold">AI</span>
                  </div>
                  <div className="bg-white text-gray-900 px-4 py-3 rounded-2xl shadow-sm border border-gray-100">
                    <div className="flex items-center space-x-2">
                      <div className="typing-indicator"></div>
                      <span className="text-sm text-gray-600">Thinking...</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>

        {/* Input Section */}
        <div className="p-6 border-t border-gray-100">
          <div className="max-w-4xl mx-auto">
            <div className="flex items-end space-x-3">
              <div className="flex-1 bg-white rounded-2xl border border-gray-200 p-4 shadow-sm">
                <textarea
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="What's in your mind?..."
                  disabled={!session && !conversations.length}
                  className="w-full border-none outline-none resize-none text-gray-900 placeholder-gray-400 text-sm"
                  rows={1}
                  style={{ minHeight: '24px', maxHeight: '120px' }}
                />
              </div>
              <button
                onClick={handleSendMessage}
                disabled={!session && !conversations.length || !inputMessage.trim() || isLoading}
                className="bg-gradient-to-r from-purple-600 to-blue-600 text-white p-4 rounded-2xl hover:from-purple-700 hover:to-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all flex items-center justify-center"
              >
                <Send className="h-5 w-5" />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Upgrade to Pro Tab */}
      <div className="fixed right-0 top-1/2 transform -translate-y-1/2">
        <div className="bg-gradient-to-b from-purple-600 to-blue-600 text-white p-4 rounded-l-2xl shadow-lg cursor-pointer hover:from-purple-700 hover:to-blue-700 transition-all">
          <div className="writing-mode-vertical flex items-center space-x-2">
            <Star className="h-5 w-5" />
            <span className="text-sm font-medium" style={{ writingMode: 'vertical-rl', textOrientation: 'mixed' }}>
              Upgrade to Pro
            </span>
          </div>
        </div>
      </div>

      {/* Hidden file input */}
      <input
        ref={fileInputRef}
        type="file"
        accept=".pdf,.docx,.doc,.txt"
        onChange={handleFileUpload}
        className="hidden"
      />
    </div>
  );
}

export default App;
