import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { Upload, Send, FileText, Bot, User, AlertCircle, Plus, Search, Settings, Trash2, Edit, MoreVertical, Star } from 'lucide-react';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'bot';
  timestamp: Date;
  isRelated?: boolean;
  confidence?: number;
}

interface Session {
  sessionId: string;
  filename: string;
}

function App() {
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [session, setSession] = useState<Session | null>(null);
  const [uploadedFile, setUploadedFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [conversations, setConversations] = useState<Array<{id: string, title: string, date: Date}>>([]);

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Add welcome message
  useEffect(() => {
    if (messages.length === 0) {
      setMessages([
        {
          id: '1',
          text: "ðŸ‘‹ Hi there! I'm your AI document assistant. I can help you understand and analyze any document you upload. Just share a PDF, Word document, or text file, and I'll be happy to chat with you about its content!",
          sender: 'bot',
          timestamp: new Date(),
        }
      ]);
    }
  }, []);

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Validate file type
    const allowedTypes = ['application/pdf', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document', 'text/plain'];
    if (!allowedTypes.includes(file.type)) {
      setError('Please upload a PDF, Word document, or text file.');
      return;
    }

    setUploadedFile(file);
    setIsUploading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setSession({
        sessionId: response.data.session_id,
        filename: response.data.filename,
      });

      // Add to conversations
      const newConversation = {
        id: response.data.session_id,
        title: response.data.filename,
        date: new Date()
      };
      setConversations(prev => [newConversation, ...prev]);

      // Add success message
      setMessages(prev => [
        ...prev,
        {
          id: Date.now().toString(),
          text: `ðŸ“„ Great! I've uploaded "${response.data.filename}" and I'm ready to help you understand it. Feel free to ask me anything about the document - I can summarize, explain, count items, or answer any questions you have!`,
          sender: 'bot',
          timestamp: new Date(),
        }
      ]);

    } catch (error: any) {
      setError(error.response?.data?.detail || 'Failed to upload document. Please try again.');
    } finally {
      setIsUploading(false);
    }
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim() || !session) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputMessage,
      sender: 'user',
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);
    setError(null);

    try {
      const response = await axios.post('/chat', {
        message: inputMessage,
        session_id: session.sessionId,
      });

      const botMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: response.data.response,
        sender: 'bot',
        timestamp: new Date(),
        isRelated: response.data.is_related,
        confidence: response.data.confidence,
      };

      setMessages(prev => [...prev, botMessage]);

    } catch (error: any) {
      setError(error.response?.data?.detail || 'Failed to get response. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSendMessage();
    }
  };

  const resetSession = () => {
    setSession(null);
    setUploadedFile(null);
    setMessages([
      {
        id: '1',
        text: "ðŸ‘‹ Hi there! I'm your AI document assistant. I can help you understand and analyze any document you upload. Just share a PDF, Word document, or text file, and I'll be happy to chat with you about its content!",
        sender: 'bot',
        timestamp: new Date(),
      }
    ]);
  };

  const clearAllConversations = () => {
    setConversations([]);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-blue-100">
      <div className="flex h-screen">
        {/* Left Sidebar */}
        <div className="w-80 bg-white shadow-lg flex flex-col">
          {/* Header */}
          <div className="p-6 border-b border-gray-100">
            <h1 className="text-2xl font-bold text-gray-900 mb-4">DOC CHAT A.I.+</h1>
            <button className="w-full bg-gradient-to-r from-purple-600 to-blue-600 text-white px-4 py-3 rounded-xl hover:from-purple-700 hover:to-blue-700 transition-all duration-200 flex items-center justify-center space-x-2 font-medium">
              <Plus className="h-5 w-5" />
              <span>New chat</span>
            </button>
            <div className="mt-4 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search conversations..."
                className="w-full pl-10 pr-4 py-2 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent"
              />
            </div>
          </div>

          {/* Conversations */}
          <div className="flex-1 overflow-y-auto p-4">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-sm font-semibold text-gray-700">Your conversations</h2>
              <button 
                onClick={clearAllConversations}
                className="text-xs text-gray-500 hover:text-gray-700"
              >
                Clear All
              </button>
            </div>

            <div className="space-y-2">
              {conversations.map((conv, index) => (
                <div
                  key={conv.id}
                  className={`p-3 rounded-lg cursor-pointer transition-all duration-200 ${
                    session?.sessionId === conv.id 
                      ? 'bg-purple-50 border border-purple-200' 
                      : 'hover:bg-gray-50'
                  }`}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3 flex-1 min-w-0">
                      <div className="h-8 w-8 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center">
                        <FileText className="h-4 w-4 text-white" />
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium text-gray-900 truncate">{conv.title}</p>
                        <p className="text-xs text-gray-500">{conv.date.toLocaleDateString()}</p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-1">
                      <button className="p-1 hover:bg-gray-200 rounded">
                        <Edit className="h-3 w-3 text-gray-400" />
                      </button>
                      <button className="p-1 hover:bg-gray-200 rounded">
                        <Trash2 className="h-3 w-3 text-gray-400" />
                      </button>
                      <button className="p-1 hover:bg-gray-200 rounded">
                        <MoreVertical className="h-3 w-3 text-gray-400" />
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Bottom Section */}
          <div className="p-4 border-t border-gray-100">
            <button className="w-full flex items-center space-x-3 p-3 hover:bg-gray-50 rounded-lg transition-colors">
              <Settings className="h-5 w-5 text-gray-500" />
              <span className="text-sm text-gray-700">Settings</span>
            </button>
            <div className="flex items-center space-x-3 p-3">
              <div className="h-8 w-8 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center">
                <span className="text-white text-sm font-medium">A</span>
              </div>
              <div>
                <p className="text-sm font-medium text-gray-900">Admin User</p>
                <p className="text-xs text-gray-500">admin@example.com</p>
              </div>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          {/* File Upload Section */}
          {!session && (
            <div className="flex-1 flex items-center justify-center p-8">
              <div className="bg-white rounded-2xl shadow-xl border border-gray-100 p-8 max-w-md w-full">
                <div className="text-center">
                  <div className="h-16 w-16 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center mx-auto mb-6">
                    <Upload className="h-8 w-8 text-white" />
                  </div>
                  <h2 className="text-xl font-semibold text-gray-900 mb-3">
                    Upload a Document
                  </h2>
                  <p className="text-gray-600 mb-6">
                    Upload a PDF, Word document, or text file to start chatting about its content.
                  </p>
                  <input
                    ref={fileInputRef}
                    type="file"
                    accept=".pdf,.docx,.doc,.txt"
                    onChange={handleFileUpload}
                    className="hidden"
                  />
                  <button
                    onClick={() => fileInputRef.current?.click()}
                    disabled={isUploading}
                    className="w-full bg-gradient-to-r from-purple-600 to-blue-600 text-white px-6 py-3 rounded-xl hover:from-purple-700 hover:to-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center space-x-2 font-medium transition-all duration-200"
                  >
                    {isUploading ? (
                      <>
                        <div className="typing-indicator"></div>
                        <span>Uploading...</span>
                      </>
                    ) : (
                      <>
                        <Upload className="h-5 w-5" />
                        <span>Choose File</span>
                      </>
                    )}
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Chat Interface */}
          {session && (
            <div className="flex-1 flex flex-col">
              {/* Chat Header */}
              <div className="bg-white border-b border-gray-100 p-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="h-8 w-8 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center">
                      <FileText className="h-4 w-4 text-white" />
                    </div>
                    <div>
                      <h3 className="font-medium text-gray-900">{session.filename}</h3>
                      <p className="text-sm text-gray-500">Document Analysis</p>
                    </div>
                  </div>
                  <button
                    onClick={resetSession}
                    className="text-sm text-gray-600 hover:text-gray-800 font-medium"
                  >
                    New Document
                  </button>
                </div>
              </div>

              {/* Error Display */}
              {error && (
                <div className="mx-4 mt-4 bg-red-50 border border-red-200 rounded-xl p-4 flex items-center space-x-3">
                  <AlertCircle className="h-5 w-5 text-red-500" />
                  <span className="text-red-700">{error}</span>
                </div>
              )}

              {/* Chat Messages */}
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div className={`max-w-xs lg:max-w-md px-4 py-3 rounded-2xl ${
                      message.sender === 'user'
                        ? 'bg-gradient-to-r from-purple-600 to-blue-600 text-white'
                        : 'bg-white border border-gray-200 text-gray-900 shadow-sm'
                    }`}>
                      <div className="flex items-start space-x-3">
                        {message.sender === 'bot' && (
                          <div className="h-6 w-6 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center flex-shrink-0">
                            <Bot className="h-3 w-3 text-white" />
                          </div>
                        )}
                        <div className="flex-1">
                          <div className="text-sm whitespace-pre-line leading-relaxed">{message.text}</div>
                          {message.sender === 'bot' && message.isRelated !== undefined && (
                            <div className="mt-2 text-xs">
                              {message.isRelated ? (
                                <span className="text-green-600">âœ“ Related to document</span>
                              ) : (
                                <span className="text-orange-600">âš  Not related to document</span>
                              )}
                            </div>
                          )}
                        </div>
                        {message.sender === 'user' && (
                          <div className="h-6 w-6 bg-white rounded-full flex items-center justify-center flex-shrink-0">
                            <User className="h-3 w-3 text-purple-600" />
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                ))}

                {/* Loading indicator */}
                {isLoading && (
                  <div className="flex justify-start">
                    <div className="bg-white border border-gray-200 text-gray-900 px-4 py-3 rounded-2xl shadow-sm">
                      <div className="flex items-center space-x-3">
                        <div className="h-6 w-6 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center">
                          <Bot className="h-3 w-3 text-white" />
                        </div>
                        <div className="typing-indicator"></div>
                        <span className="text-sm text-gray-600">Thinking...</span>
                      </div>
                    </div>
                  </div>
                )}

                {/* Suggestion buttons */}
                {session && messages.length > 1 && !isLoading && (
                  <div className="flex flex-wrap gap-2 justify-start">
                    <button
                      onClick={() => setInputMessage("What are the main findings?")}
                      className="text-xs bg-blue-50 text-blue-700 px-3 py-2 rounded-full hover:bg-blue-100 transition-colors border border-blue-200"
                    >
                      What are the main findings?
                    </button>
                    <button
                      onClick={() => setInputMessage("How many recommendations are there?")}
                      className="text-xs bg-green-50 text-green-700 px-3 py-2 rounded-full hover:bg-green-100 transition-colors border border-green-200"
                    >
                      Count recommendations
                    </button>
                    <button
                      onClick={() => setInputMessage("Can you summarize this document?")}
                      className="text-xs bg-purple-50 text-purple-700 px-3 py-2 rounded-full hover:bg-purple-100 transition-colors border border-purple-200"
                    >
                      Summarize document
                    </button>
                    <button
                      onClick={() => setInputMessage("What are the key conclusions?")}
                      className="text-xs bg-orange-50 text-orange-700 px-3 py-2 rounded-full hover:bg-orange-100 transition-colors border border-orange-200"
                    >
                      Key conclusions
                    </button>
                  </div>
                )}

                <div ref={messagesEndRef} />
              </div>

              {/* Input Section */}
              <div className="bg-white border-t border-gray-100 p-4">
                <div className="flex space-x-3">
                  <input
                    type="text"
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="What's in your mind?..."
                    disabled={!session || isLoading}
                    className="flex-1 px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent disabled:bg-gray-50 disabled:cursor-not-allowed text-sm"
                  />
                  <button
                    onClick={handleSendMessage}
                    disabled={!session || !inputMessage.trim() || isLoading}
                    className="bg-gradient-to-r from-purple-600 to-blue-600 text-white px-4 py-3 rounded-xl hover:from-purple-700 hover:to-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center transition-all duration-200"
                  >
                    <Send className="h-4 w-4" />
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Right Sidebar - Upgrade Pro */}
        <div className="w-16 bg-gradient-to-b from-purple-600 to-blue-600 flex flex-col items-center justify-center">
          <div className="transform -rotate-90 text-white font-medium flex items-center space-x-2">
            <Star className="h-4 w-4" />
            <span>Upgrade to Pro</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
